    private void btnResetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnResetActionPerformed
        // TODO add your handling code here:
        zahler.reset();
        txtStand.setText(zahler.getStand() + "");
    }//GEN-LAST:event_btnResetActionPerformed

    private void txtStandActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtStandActionPerformed
        // TODO add your handling code here:
        int newWert = Integer.parseInt(txtStand.getText());
        zahler.setStand(newWert);
    }//GEN-LAST:event_txtStandActionPerformed

